'use client';

import { ContentEditable } from '@lexical/react/LexicalContentEditable';
import { LexicalErrorBoundary } from '@lexical/react/LexicalErrorBoundary';
import { LexicalExtensionComposer } from '@lexical/react/LexicalExtensionComposer';
import { RichTextPlugin } from '@lexical/react/LexicalRichTextPlugin';
import { EditorState, LexicalEditor } from 'lexical';
import { ReactNode, useState } from 'react';
import EditorToolbarPlugin from './editor-toolbar-plugin';
import EditorBlockControlPlugin from './editor-block-control-plugin';
import { OnChangePlugin } from '@lexical/react/LexicalOnChangePlugin';
import { Editor } from './lib/Editor';

export function EditorView({
	editor,
	className,
	placeholder,
	showBlockHandle = true,
	showToolbar = true,
	onChange,
	children,
}: {
	editor: Editor;
	className?: string;
	placeholder?: string;
	showBlockHandle?: boolean;
	showToolbar?: boolean;
	onChange?: (
		editorState: EditorState,
		editor: LexicalEditor,
		tags: Set<string>,
	) => void;
	children?: ReactNode;
}) {
	const [floatingAnchorElem, setFloatingAnchorElem] =
		useState<HTMLDivElement | null>(null);

	const onRef = (_floatingAnchorElem: HTMLDivElement) => {
		if (_floatingAnchorElem !== null) {
			setFloatingAnchorElem(_floatingAnchorElem);
		}
	};

	const editorPlaceholder =
		placeholder ?? "Enter some text or type '/' for commands";

	return (
		<LexicalExtensionComposer
			extension={editor.lexicalExtension}
			contentEditable={null}
		>
			<div
				className={`relative font-normal leading-[1.7] text-black ${className ?? ''}`}
			>
				<div className="relative block bg-white">
					<RichTextPlugin
						contentEditable={
							<div className="relative z-0 flex min-h-37.5 max-w-full resize-y border-0 outline-none">
								<div
									ref={onRef}
									className="relative z-0 max-w-full flex-auto resize-y"
								>
									<ContentEditable
										className={`relative min-h-37.5 resize-none px-2.5 py-3.75 text-[15px] caret-[#444] outline-none [tab-size:1] ${showBlockHandle ? 'ml-12' : ''}`}
									/>
								</div>
							</div>
						}
						placeholder={
							<div
								className={`pointer-events-none absolute top-3.75 inline-block select-none overflow-hidden text-ellipsis text-[15px] text-[#999] ${showBlockHandle ? 'left-14.5' : 'left-2.5'}`}
							>
								{editorPlaceholder}
							</div>
						}
						ErrorBoundary={LexicalErrorBoundary}
					/>
					{floatingAnchorElem && showToolbar && (
						<EditorToolbarPlugin
							anchorElem={floatingAnchorElem}
							options={editor.blockPickerOptions}
						/>
					)}
					{floatingAnchorElem && showBlockHandle && (
						<EditorBlockControlPlugin
							anchorElem={floatingAnchorElem}
							options={editor.blockPickerOptions}
						/>
					)}
					{children}
					{onChange && <OnChangePlugin onChange={onChange} />}
				</div>
			</div>
		</LexicalExtensionComposer>
	);
}

'use client';

import { mergeRegister } from '@lexical/utils';
import {
	$getSelection,
	$isRangeSelection,
	COMMAND_PRIORITY_LOW,
	FORMAT_TEXT_COMMAND,
	getDOMSelection,
	LexicalEditor,
	SELECTION_CHANGE_COMMAND,
	TextFormatType,
} from 'lexical';
import { JSX, useCallback, useEffect, useRef } from 'react';
import { createPortal } from 'react-dom';

import {
	Bold,
	Italic,
	Underline,
	Strikethrough,
	ChevronDown,
} from 'lucide-react';

import { getDOMRangeRect, setFloatingElemPosition } from './lib/editor-utils';
import { ToggleGroup, ToggleGroupItem } from '@/components/ui/toggle-group';
import {
	DropdownMenu,
	DropdownMenuContent,
	DropdownMenuItem,
	DropdownMenuTrigger,
} from '@/components/ui/dropdown-menu';
import { Button } from '@/components/ui/button';
import type { BlockPickerOption } from './lib/BlockPickerOption';
import { useEditorToolbar } from './hooks/use-editor-toolbar';

export default function EditorToolbar({
	editor,
	anchorElem,
	options,
}: {
	editor: LexicalEditor;
	anchorElem: HTMLElement;
	options: BlockPickerOption[];
}): JSX.Element | null {
	const state = useEditorToolbar(editor);
	const popupCharStylesEditorRef = useRef<HTMLDivElement | null>(null);

	const { isBold, isItalic, isUnderline, isStrikethrough, blockType } = state;

	const currentBlock = options.find((opt) => opt.key === blockType);

	// Callback ref to position toolbar immediately when element is created
	const setPopupRef = useCallback(
		(elem: HTMLDivElement | null) => {
			popupCharStylesEditorRef.current = elem;
			if (elem) {
				// Position immediately when ref is set
				editor.getEditorState().read(() => {
					const selection = $getSelection();
					const nativeSelection = getDOMSelection(editor._window);
					const rootElement = editor.getRootElement();

					if (
						selection !== null &&
						nativeSelection !== null &&
						!nativeSelection.isCollapsed &&
						rootElement !== null &&
						rootElement.contains(nativeSelection.anchorNode)
					) {
						const rangeRect = getDOMRangeRect(
							nativeSelection,
							rootElement,
						);
						setFloatingElemPosition(
							rangeRect,
							elem,
							anchorElem,
							false,
						);
					}
				});
			}
		},
		[editor, anchorElem],
	);

	function mouseMoveListener(e: MouseEvent) {
		if (
			popupCharStylesEditorRef.current &&
			(e.buttons === 1 || e.buttons === 2)
		) {
			if (
				popupCharStylesEditorRef.current.style.pointerEvents !== 'none'
			) {
				const elementUnderMouse = document.elementFromPoint(
					e.clientX,
					e.clientY,
				);
				if (
					elementUnderMouse &&
					!popupCharStylesEditorRef.current.contains(
						elementUnderMouse,
					)
				) {
					popupCharStylesEditorRef.current.style.pointerEvents =
						'none';
				}
			}
		}
	}

	function mouseUpListener() {
		if (!popupCharStylesEditorRef.current) return;
		if (popupCharStylesEditorRef.current.style.pointerEvents !== 'auto') {
			popupCharStylesEditorRef.current.style.pointerEvents = 'auto';
		}
	}

	useEffect(() => {
		if (!popupCharStylesEditorRef.current) return;

		document.addEventListener('mousemove', mouseMoveListener);
		document.addEventListener('mouseup', mouseUpListener);

		return () => {
			document.removeEventListener('mousemove', mouseMoveListener);
			document.removeEventListener('mouseup', mouseUpListener);
		};
	}, []);

	const $updateTextFormatEditorToolbar = useCallback(() => {
		const selection = $getSelection();
		const popupElem = popupCharStylesEditorRef.current;
		const nativeSelection = getDOMSelection(editor._window);

		if (popupElem === null) return;

		const rootElement = editor.getRootElement();
		if (
			selection !== null &&
			nativeSelection !== null &&
			!nativeSelection.isCollapsed &&
			rootElement !== null &&
			rootElement.contains(nativeSelection.anchorNode)
		) {
			const rangeRect = getDOMRangeRect(nativeSelection, rootElement);
			setFloatingElemPosition(rangeRect, popupElem, anchorElem, false);
		}
	}, [editor, anchorElem]);

	useEffect(() => {
		const scrollerElem = anchorElem.parentElement;

		const update = () => {
			editor.getEditorState().read(() => {
				$updateTextFormatEditorToolbar();
			});
		};

		window.addEventListener('resize', update);
		scrollerElem?.addEventListener('scroll', update);

		return () => {
			window.removeEventListener('resize', update);
			scrollerElem?.removeEventListener('scroll', update);
		};
	}, [editor, $updateTextFormatEditorToolbar, anchorElem]);

	useEffect(() => {
		editor.getEditorState().read(() => {
			$updateTextFormatEditorToolbar();
		});

		return mergeRegister(
			editor.registerUpdateListener(({ editorState }) => {
				editorState.read(() => {
					$updateTextFormatEditorToolbar();
				});
			}),
			editor.registerCommand(
				SELECTION_CHANGE_COMMAND,
				() => {
					$updateTextFormatEditorToolbar();
					return false;
				},
				COMMAND_PRIORITY_LOW,
			),
		);
	}, [editor, $updateTextFormatEditorToolbar]);

	if (!state.isVisible) return null;

	const handleValueChange = (values: string[]) => {
		const formats: TextFormatType[] = [
			'bold',
			'italic',
			'underline',
			'strikethrough',
		];
		formats.forEach((format) => {
			const isActive =
				format === 'bold'
					? isBold
					: format === 'italic'
						? isItalic
						: format === 'underline'
							? isUnderline
							: isStrikethrough;
			const shouldBeActive = values.includes(format);
			if (isActive !== shouldBeActive) {
				editor.dispatchCommand(FORMAT_TEXT_COMMAND, format);
			}
		});
	};

	// Filter to only block-type options (exclude alignment, dividers)
	const blockOptions = options.filter(
		(opt) =>
			opt.category === 'basic' ||
			opt.category === 'headings' ||
			opt.category === 'lists' ||
			opt.category === 'quotes',
	);

	return createPortal(
		<div
			ref={setPopupRef}
			className="absolute z-50 flex items-center gap-0.5 rounded-md border bg-popover p-1 shadow-md animate-in fade-in-0 zoom-in-95 slide-in-from-bottom-2 duration-150"
		>
			<DropdownMenu>
				<DropdownMenuTrigger asChild>
					<Button variant="ghost">
						{currentBlock?.icon}
						<span>{currentBlock?.title ?? 'Paragraph'}</span>
						<ChevronDown className="h-3 w-3" />
					</Button>
				</DropdownMenuTrigger>
				<DropdownMenuContent align="start">
					{blockOptions.map((option) => (
						<DropdownMenuItem
							key={option.key}
							onSelect={() => {
								option.insert({ editor, queryString: '' });
								editor.update(() => {
									const selection = $getSelection();
									if ($isRangeSelection(selection)) {
										selection.focus.set(
											selection.anchor.key,
											selection.anchor.offset,
											selection.anchor.type,
										);
									}
								});
							}}
							className={
								blockType === option.key ? 'bg-accent' : ''
							}
						>
							<span className="mr-2 h-4 w-4">{option.icon}</span>
							{option.title}
						</DropdownMenuItem>
					))}
				</DropdownMenuContent>
			</DropdownMenu>
			{editor.isEditable() && (
				<ToggleGroup
					type="multiple"
					spacing={1}
					value={[
						...(isBold ? ['bold'] : []),
						...(isItalic ? ['italic'] : []),
						...(isUnderline ? ['underline'] : []),
						...(isStrikethrough ? ['strikethrough'] : []),
					]}
					onValueChange={handleValueChange}
				>
					<ToggleGroupItem
						value="bold"
						title="Bold"
						aria-label="Format text as bold"
					>
						<Bold className="h-2 w-2" />
					</ToggleGroupItem>

					<ToggleGroupItem
						value="italic"
						title="Italic"
						aria-label="Format text as italics"
					>
						<Italic className="h-2 w-2" />
					</ToggleGroupItem>

					<ToggleGroupItem
						value="underline"
						title="Underline"
						aria-label="Format text to underlined"
					>
						<Underline className="h-2 w-2" />
					</ToggleGroupItem>

					<ToggleGroupItem
						value="strikethrough"
						title="Strikethrough"
						aria-label="Format text with a strikethrough"
					>
						<Strikethrough className="h-2 w-2" />
					</ToggleGroupItem>
				</ToggleGroup>
			)}
		</div>,
		anchorElem,
	);
}

import type { JSX } from 'react';

import { useLexicalComposerContext } from '@lexical/react/LexicalComposerContext';
import EditorToolbar from './editor-toolbar';
import { BlockPickerOption } from './lib/BlockPickerOption';

export default function EditorToolbarPlugin({
	anchorElem = document.body,
	options,
}: {
	anchorElem?: HTMLElement;
	options: BlockPickerOption[];
}): JSX.Element | null {
	const [editor] = useLexicalComposerContext();
	return (
		<EditorToolbar
			editor={editor}
			anchorElem={anchorElem}
			options={options}
		/>
	);
}

'use client';

import type { NodeKey } from 'lexical';
import type { JSX } from 'react';

import { DraggableBlockPlugin_EXPERIMENTAL } from '@lexical/react/LexicalDraggableBlockPlugin';
import { useLexicalComposerContext } from '@lexical/react/LexicalComposerContext';
import {
	$createParagraphNode,
	$createTextNode,
	$getNearestNodeFromDOMNode,
	$getNodeByKey,
	$isParagraphNode,
	$isTextNode,
} from 'lexical';
import { useCallback, useEffect, useMemo, useRef, useState } from 'react';
import { createPortal } from 'react-dom';
import { GripVertical, Plus } from 'lucide-react';

import { BlockPickerOption } from './lib/BlockPickerOption';
import { BlockPickerMenu } from './block-picker-menu';

const DRAGGABLE_BLOCK_MENU_CLASSNAME = 'draggable-block-menu';

type PickerState = {
	paragraphKey: NodeKey;
};

type BlockControlPluginProps = {
	anchorElem?: HTMLElement;
	options?: BlockPickerOption[];
};

function isOnMenu(element: HTMLElement): boolean {
	return !!element.closest(`.${DRAGGABLE_BLOCK_MENU_CLASSNAME}`);
}

export default function EditorBlockControlPlugin({
	anchorElem = document.body,
	options = [],
}: BlockControlPluginProps): JSX.Element {
	const [editor] = useLexicalComposerContext();
	const menuRef = useRef<HTMLDivElement>(null);
	const pickerRef = useRef<HTMLDivElement>(null);
	const targetLineRef = useRef<HTMLDivElement>(null);
	const [draggableElement, setDraggableElement] =
		useState<HTMLElement | null>(null);
	const [pickerState, setPickerState] = useState<PickerState | null>(null);
	const [isPickerOpen, setIsPickerOpen] = useState(false);
	const [queryString, setQueryString] = useState('');
	const [highlightedIndex, setHighlightedIndex] = useState(0);
	const [pickerPosition, setPickerPosition] = useState<{
		left: number;
		top: number;
		anchor: 'top' | 'bottom';
	} | null>(null);

	const filteredOptions = useMemo(() => {
		if (!queryString) return options;
		const regex = new RegExp(queryString, 'i');
		return options.filter(
			(option) =>
				regex.test(option.title) ||
				option.keywords.some((keyword) => regex.test(keyword)),
		);
	}, [options, queryString]);

	const clampedIndex =
		filteredOptions.length > 0
			? Math.min(highlightedIndex, filteredOptions.length - 1)
			: 0;

	// Close on click outside
	useEffect(() => {
		if (!isPickerOpen) return;
		const handleClickOutside = (event: MouseEvent) => {
			const target = event.target as Node | null;
			if (
				(pickerRef.current && pickerRef.current.contains(target)) ||
				(menuRef.current && menuRef.current.contains(target))
			) {
				return;
			}
			setIsPickerOpen(false);
			setPickerState(null);
		};
		document.addEventListener('mousedown', handleClickOutside);
		return () => {
			document.removeEventListener('mousedown', handleClickOutside);
		};
	}, [isPickerOpen]);

	const selectOption = useCallback(
		(option: BlockPickerOption) => {
			if (!pickerState) {
				setIsPickerOpen(false);
				return;
			}
			setIsPickerOpen(false);
			setPickerState(null);
			editor.update(() => {
				const node = $getNodeByKey(pickerState.paragraphKey);
				if (!node || !$isParagraphNode(node)) return;

				// Select into the existing paragraph so the insert replaces it
				const firstChild = node.getFirstChild();
				if ($isTextNode(firstChild)) {
					firstChild.select();
				}

				option.insert({ editor, queryString });

				// Clean up empty placeholder if the insert didn't use it
				const latest = node.getLatest();
				if ($isParagraphNode(latest)) {
					const onlyChild = latest.getFirstChild();
					if (
						$isTextNode(onlyChild) &&
						onlyChild.getTextContent().length === 0 &&
						latest.getChildrenSize() === 1
					) {
						latest.remove();
					}
				}
			});
		},
		[editor, pickerState, queryString],
	);

	// Keyboard navigation
	useEffect(() => {
		if (!isPickerOpen) return;
		const handleKeyDown = (event: KeyboardEvent) => {
			if (!filteredOptions.length) return;
			if (event.key === 'ArrowDown') {
				event.preventDefault();
				setHighlightedIndex((i) =>
					i + 1 >= filteredOptions.length ? 0 : i + 1,
				);
			} else if (event.key === 'ArrowUp') {
				event.preventDefault();
				setHighlightedIndex((i) =>
					i - 1 < 0 ? filteredOptions.length - 1 : i - 1,
				);
			} else if (event.key === 'Enter') {
				event.preventDefault();
				const option = filteredOptions[clampedIndex];
				if (option) selectOption(option);
			} else if (event.key === 'Escape') {
				event.preventDefault();
				setIsPickerOpen(false);
				setPickerState(null);
			}
		};
		window.addEventListener('keydown', handleKeyDown);
		return () => window.removeEventListener('keydown', handleKeyDown);
	}, [clampedIndex, isPickerOpen, filteredOptions, selectOption]);

	function openPicker() {
		if (!draggableElement || !editor) return;

		let paragraphKey: NodeKey | null = null;

		editor.update(() => {
			const node = $getNearestNodeFromDOMNode(draggableElement);
			if (!node) return;

			const paragraph = $createParagraphNode();
			const textNode = $createTextNode('');
			paragraph.append(textNode);
			node.insertAfter(paragraph);
			textNode.select();
			paragraphKey = paragraph.getKey();
		});

		if (!paragraphKey) return;

		// Wait for DOM to update, then position picker at the new paragraph
		requestAnimationFrame(() => {
			const paragraphDom = editor.getElementByKey(paragraphKey!);
			if (!paragraphDom) return;

			const rect = paragraphDom.getBoundingClientRect();
			const PICKER_HEIGHT = 300;
			const spaceBelow = window.innerHeight - rect.bottom;
			const showAbove =
				spaceBelow < PICKER_HEIGHT && rect.top > PICKER_HEIGHT;

			setPickerPosition({
				left: rect.left,
				top: showAbove ? rect.top : rect.bottom + 4,
				anchor: showAbove ? 'bottom' : 'top',
			});
			setPickerState({ paragraphKey: paragraphKey! });
			setQueryString('');
			setHighlightedIndex(0);
			setIsPickerOpen(true);
		});
	}

	return (
		<>
			{isPickerOpen && pickerPosition
				? createPortal(
						<div
							ref={pickerRef}
							className="fixed z-50"
							style={{
								left: pickerPosition.left,
								...(pickerPosition.anchor === 'bottom'
									? {
											bottom:
												window.innerHeight -
												pickerPosition.top,
										}
									: { top: pickerPosition.top }),
							}}
						>
							<BlockPickerMenu
								options={filteredOptions}
								selectedIndex={clampedIndex}
								onSelectOption={(option) => {
									selectOption(option);
								}}
								onSetHighlightedIndex={setHighlightedIndex}
								queryString={queryString}
								onQueryChange={(q) => {
									setQueryString(q);
									setHighlightedIndex(0);
								}}
							/>
						</div>,
						document.body,
					)
				: null}
			<DraggableBlockPlugin_EXPERIMENTAL
				anchorElem={anchorElem}
				menuRef={menuRef}
				targetLineRef={targetLineRef}
				menuComponent={
					<div
						ref={menuRef}
						className={`${DRAGGABLE_BLOCK_MENU_CLASSNAME} group flex items-center gap-0.5 rounded p-0.5 px-px opacity-0 absolute left-0 top-0 will-change-transform`}
					>
						<button
							type="button"
							title="Click to add below, hold Alt/Ctrl to add above"
							className="flex items-center justify-center w-6 h-6 p-0 border-none bg-transparent cursor-pointer opacity-30 rounded group-hover:opacity-60 hover:opacity-100! hover:bg-neutral-200"
							aria-label="Add block"
							onClick={openPicker}
						>
							<Plus size={18} />
						</button>
						<div className="flex items-center justify-center w-6 h-6 opacity-30 cursor-grab active:cursor-grabbing rounded group-hover:opacity-60 hover:opacity-100! hover:bg-neutral-200">
							<GripVertical size={18} />
						</div>
					</div>
				}
				targetLineComponent={
					<div
						ref={targetLineRef}
						className="ib-block-control-target-line pointer-events-none bg-sky-400 h-1 absolute left-0 top-0 opacity-0 will-change-transform"
					/>
				}
				isOnMenu={isOnMenu}
				onElementChanged={setDraggableElement}
			/>
		</>
	);
}

import type { JSX } from 'react';

import { useLexicalComposerContext } from '@lexical/react/LexicalComposerContext';
import {
	LexicalTypeaheadMenuPlugin,
	useBasicTypeaheadTriggerMatch,
} from '@lexical/react/LexicalTypeaheadMenuPlugin';
import { TextNode } from 'lexical';
import { useCallback, useMemo, useState } from 'react';
import * as ReactDOM from 'react-dom';

import { BlockPickerOption } from './lib/BlockPickerOption';
import { BlockPickerMenu } from './block-picker-menu';

type BlockPickerPluginProps = {
	options: BlockPickerOption[];
};

export default function BlockPickerPlugin({
	options,
}: BlockPickerPluginProps): JSX.Element {
	const [editor] = useLexicalComposerContext();

	const [queryString, setQueryString] = useState<string | null>(null);

	const checkForTriggerMatch = useBasicTypeaheadTriggerMatch('/', {
		allowWhitespace: true,
		minLength: 0,
	});

	const filteredOptions = useMemo(() => {
		if (!queryString) return options;

		const regex = new RegExp(queryString, 'i');

		return options.filter((option) => {
			return (
				regex.test(option.title) ||
				option.keywords.some((keyword: string) => regex.test(keyword))
			);
		});
	}, [options, queryString]);

	const onSelectOption = useCallback(
		(
			selectedOption: BlockPickerOption,
			nodeToRemove: TextNode | null,
			closeMenu: () => void,
			matchingString: string,
		) => {
			editor.update(() => {
				nodeToRemove?.remove();
				selectedOption.insert({
					editor,
					queryString: matchingString,
				});
				closeMenu();
			});
		},
		[editor],
	);

	return (
		<>
			<LexicalTypeaheadMenuPlugin<BlockPickerOption>
				onQueryChange={setQueryString}
				onSelectOption={onSelectOption}
				triggerFn={checkForTriggerMatch}
				options={filteredOptions}
				menuRenderFn={(
					anchorElementRef,
					{
						selectedIndex,
						selectOptionAndCleanUp,
						setHighlightedIndex,
					},
				) =>
					anchorElementRef.current && filteredOptions.length
						? ReactDOM.createPortal(
								<BlockPickerMenu
									options={filteredOptions}
									selectedIndex={selectedIndex}
									onSelectOption={(option) => {
										selectOptionAndCleanUp(option);
									}}
									onSetHighlightedIndex={setHighlightedIndex}
								/>,
								anchorElementRef.current,
							)
						: null
				}
			/>
		</>
	);
}

import { useRef, useEffect } from 'react';

import { BlockPickerOption } from './lib/BlockPickerOption';
import { Input } from '@/components/ui/input';
import { BlockPickerMenuItem } from './block-picker-menu-item';

type BlockPickerMenuProps = {
	options: BlockPickerOption[];
	selectedIndex: number | null;
	onSelectOption: (option: BlockPickerOption, index: number) => void;
	onSetHighlightedIndex: (index: number) => void;
	queryString?: string;
	onQueryChange?: (query: string) => void;
};

export function BlockPickerMenu({
	options,
	selectedIndex,
	onSelectOption,
	onSetHighlightedIndex,
	queryString,
	onQueryChange,
}: BlockPickerMenuProps) {
	const inputRef = useRef<HTMLInputElement>(null);

	useEffect(() => {
		inputRef.current?.focus();
	}, []);

	return (
		<div className="bg-popover text-popover-foreground animate-in fade-in-0 zoom-in-95 slide-in-from-top-2 z-50 min-w-50 rounded-md border p-1 shadow-md">
			{onQueryChange != null && (
				<div className="p-1 pb-0 mb-1">
					<Input
						ref={inputRef}
						placeholder="Filter blocks..."
						value={queryString ?? ''}
						onChange={(e) => onQueryChange(e.target.value)}
						className="h-7 text-xs"
					/>
				</div>
			)}
			<div className="max-h-50 overflow-y-auto">
				{options.length > 0 ? (
					<ul className="list-none m-0 p-0 mt-1">
						{options.map((option, i: number) => (
							<BlockPickerMenuItem
								key={option.key}
								index={i}
								isSelected={selectedIndex === i}
								onClick={() => {
									onSetHighlightedIndex(i);
									onSelectOption(option, i);
								}}
								onMouseEnter={() => {
									onSetHighlightedIndex(i);
								}}
								setRefElement={option.setRefElement}
								icon={option.icon}
								title={option.title}
							/>
						))}
					</ul>
				) : (
					<p className="text-muted-foreground text-xs px-2 py-1.5 mt-1">
						No results
					</p>
				)}
			</div>
		</div>
	);
}

