import { ContentEditable } from '@lexical/react/LexicalContentEditable';
import { LexicalErrorBoundary } from '@lexical/react/LexicalErrorBoundary';
import { LexicalExtensionComposer } from '@lexical/react/LexicalExtensionComposer';
import { RichTextPlugin } from '@lexical/react/LexicalRichTextPlugin';
import { EditorState, LexicalEditor } from 'lexical';
import { ReactNode, useState } from 'react';
import EditorToolbarPlugin from '../components/editor-toolbar-plugin';
import EditorBlockControlPlugin from '../components/editor-block-control-plugin';
import { OnChangePlugin } from '@lexical/react/LexicalOnChangePlugin';
import { Editor } from '../lib/Editor';

export function EditorView({
	editor,
	className,
	placeholder,
	showBlockHandle = true,
	showToolbar = true,
	onChange,
	children,
}: {
	editor: Editor;
	className?: string;
	placeholder?: string;
	showBlockHandle?: boolean;
	showToolbar?: boolean;
	onChange?: (
		editorState: EditorState,
		editor: LexicalEditor,
		tags: Set<string>,
	) => void;
	children?: ReactNode;
}) {
	const [floatingAnchorElem, setFloatingAnchorElem] =
		useState<HTMLDivElement | null>(null);

	const onRef = (_floatingAnchorElem: HTMLDivElement) => {
		if (_floatingAnchorElem !== null) {
			setFloatingAnchorElem(_floatingAnchorElem);
		}
	};

	const editorPlaceholder =
		placeholder ?? "Enter some text or type '/' for commands";

	return (
		<LexicalExtensionComposer
			extension={editor.lexicalExtension}
			contentEditable={null}
		>
			<div
				className={`relative font-normal leading-[1.7] text-black ${className ?? ''}`}
			>
				<div className="relative block bg-white">
					<RichTextPlugin
						contentEditable={
							<div className="relative z-0 flex min-h-37.5 max-w-full resize-y border-0 outline-none">
								<div
									ref={onRef}
									className="relative z-0 max-w-full flex-auto resize-y"
								>
									<ContentEditable
										className={`relative min-h-37.5 resize-none px-2.5 py-3.75 text-[15px] caret-[#444] outline-none [tab-size:1] ${showBlockHandle ? 'ml-12' : ''}`}
									/>
								</div>
							</div>
						}
						placeholder={
							<div
								className={`pointer-events-none absolute top-3.75 inline-block select-none overflow-hidden text-ellipsis text-[15px] text-[#999] ${showBlockHandle ? 'left-14.5' : 'left-2.5'}`}
							>
								{editorPlaceholder}
							</div>
						}
						ErrorBoundary={LexicalErrorBoundary}
					/>
					{floatingAnchorElem && showToolbar && (
						<EditorToolbarPlugin anchorElem={floatingAnchorElem} />
					)}
					{floatingAnchorElem && showBlockHandle && (
						<EditorBlockControlPlugin
							anchorElem={floatingAnchorElem}
						/>
					)}
					{children}
					{onChange && <OnChangePlugin onChange={onChange} />}
				</div>
			</div>
		</LexicalExtensionComposer>
	);
}

import { mergeRegister } from '@lexical/utils';
import {
	$getSelection,
	COMMAND_PRIORITY_LOW,
	FORMAT_TEXT_COMMAND,
	getDOMSelection,
	LexicalEditor,
	SELECTION_CHANGE_COMMAND,
	TextFormatType,
} from 'lexical';
import { JSX, useCallback, useEffect, useRef } from 'react';
import { createPortal } from 'react-dom';

import {
	Bold,
	Italic,
	Underline,
	Strikethrough,
	ChevronDown,
} from 'lucide-react';

import { getDOMRangeRect, setFloatingElemPosition } from '../lib/editor-utils';
import { ToggleGroup, ToggleGroupItem } from '@/components/ui/toggle-group';
import { useEditorToolbar } from '../hooks/use-editor-toolbar';
import {
	DropdownMenu,
	DropdownMenuContent,
	DropdownMenuGroup,
	DropdownMenuItem,
	DropdownMenuLabel,
	DropdownMenuSeparator,
	DropdownMenuTrigger,
} from '@/components/ui/dropdown-menu';
import { Button } from '@/components/ui/button';

export default function EditorToolbar({
	editor,
	anchorElem,
}: {
	editor: LexicalEditor;
	anchorElem: HTMLElement;
}): JSX.Element | null {
	const state = useEditorToolbar(editor);
	const popupCharStylesEditorRef = useRef<HTMLDivElement | null>(null);

	const { isBold, isItalic, isUnderline, isStrikethrough } = state;

	// Callback ref to position toolbar immediately when element is created
	const setPopupRef = useCallback(
		(elem: HTMLDivElement | null) => {
			popupCharStylesEditorRef.current = elem;
			if (elem) {
				// Position immediately when ref is set
				editor.getEditorState().read(() => {
					const selection = $getSelection();
					const nativeSelection = getDOMSelection(editor._window);
					const rootElement = editor.getRootElement();

					if (
						selection !== null &&
						nativeSelection !== null &&
						!nativeSelection.isCollapsed &&
						rootElement !== null &&
						rootElement.contains(nativeSelection.anchorNode)
					) {
						const rangeRect = getDOMRangeRect(
							nativeSelection,
							rootElement,
						);
						setFloatingElemPosition(
							rangeRect,
							elem,
							anchorElem,
							false,
						);
					}
				});
			}
		},
		[editor, anchorElem],
	);

	function mouseMoveListener(e: MouseEvent) {
		if (
			popupCharStylesEditorRef.current &&
			(e.buttons === 1 || e.buttons === 2)
		) {
			if (
				popupCharStylesEditorRef.current.style.pointerEvents !== 'none'
			) {
				const elementUnderMouse = document.elementFromPoint(
					e.clientX,
					e.clientY,
				);
				if (
					elementUnderMouse &&
					!popupCharStylesEditorRef.current.contains(
						elementUnderMouse,
					)
				) {
					popupCharStylesEditorRef.current.style.pointerEvents =
						'none';
				}
			}
		}
	}

	function mouseUpListener() {
		if (!popupCharStylesEditorRef.current) return;
		if (popupCharStylesEditorRef.current.style.pointerEvents !== 'auto') {
			popupCharStylesEditorRef.current.style.pointerEvents = 'auto';
		}
	}

	useEffect(() => {
		if (!popupCharStylesEditorRef.current) return;

		document.addEventListener('mousemove', mouseMoveListener);
		document.addEventListener('mouseup', mouseUpListener);

		return () => {
			document.removeEventListener('mousemove', mouseMoveListener);
			document.removeEventListener('mouseup', mouseUpListener);
		};
	}, []);

	const $updateTextFormatEditorToolbar = useCallback(() => {
		const selection = $getSelection();
		const popupElem = popupCharStylesEditorRef.current;
		const nativeSelection = getDOMSelection(editor._window);

		if (popupElem === null) return;

		const rootElement = editor.getRootElement();
		if (
			selection !== null &&
			nativeSelection !== null &&
			!nativeSelection.isCollapsed &&
			rootElement !== null &&
			rootElement.contains(nativeSelection.anchorNode)
		) {
			const rangeRect = getDOMRangeRect(nativeSelection, rootElement);
			setFloatingElemPosition(rangeRect, popupElem, anchorElem, false);
		}
	}, [editor, anchorElem]);

	useEffect(() => {
		const scrollerElem = anchorElem.parentElement;

		const update = () => {
			editor.getEditorState().read(() => {
				$updateTextFormatEditorToolbar();
			});
		};

		window.addEventListener('resize', update);
		scrollerElem?.addEventListener('scroll', update);

		return () => {
			window.removeEventListener('resize', update);
			scrollerElem?.removeEventListener('scroll', update);
		};
	}, [editor, $updateTextFormatEditorToolbar, anchorElem]);

	useEffect(() => {
		editor.getEditorState().read(() => {
			$updateTextFormatEditorToolbar();
		});

		return mergeRegister(
			editor.registerUpdateListener(({ editorState }) => {
				editorState.read(() => {
					$updateTextFormatEditorToolbar();
				});
			}),
			editor.registerCommand(
				SELECTION_CHANGE_COMMAND,
				() => {
					$updateTextFormatEditorToolbar();
					return false;
				},
				COMMAND_PRIORITY_LOW,
			),
		);
	}, [editor, $updateTextFormatEditorToolbar]);

	if (!state.isVisible) return null;

	const clearSelection = () => {
		window.getSelection()?.removeAllRanges();
	};

	const handleValueChange = (values: string[]) => {
		const formats: TextFormatType[] = [
			'bold',
			'italic',
			'underline',
			'strikethrough',
		];
		formats.forEach((format) => {
			const isActive =
				format === 'bold'
					? isBold
					: format === 'italic'
						? isItalic
						: format === 'underline'
							? isUnderline
							: isStrikethrough;
			const shouldBeActive = values.includes(format);
			if (isActive !== shouldBeActive) {
				editor.dispatchCommand(FORMAT_TEXT_COMMAND, format);
			}
		});
	};

	return createPortal(
		<div
			ref={setPopupRef}
			className="absolute z-50 flex items-center gap-0.5 rounded-md border bg-popover p-1 shadow-md animate-in fade-in-0 zoom-in-95 slide-in-from-bottom-2 duration-150"
		>
			{editor.isEditable() && (
				<ToggleGroup
					type="multiple"
					spacing={1}
					value={[
						...(isBold ? ['bold'] : []),
						...(isItalic ? ['italic'] : []),
						...(isUnderline ? ['underline'] : []),
						...(isStrikethrough ? ['strikethrough'] : []),
					]}
					onValueChange={handleValueChange}
				>
					<ToggleGroupItem
						value="bold"
						title="Bold"
						aria-label="Format text as bold"
					>
						<Bold className="h-2 w-2" />
					</ToggleGroupItem>

					<ToggleGroupItem
						value="italic"
						title="Italic"
						aria-label="Format text as italics"
					>
						<Italic className="h-2 w-2" />
					</ToggleGroupItem>

					<ToggleGroupItem
						value="underline"
						title="Underline"
						aria-label="Format text to underlined"
					>
						<Underline className="h-2 w-2" />
					</ToggleGroupItem>

					<ToggleGroupItem
						value="strikethrough"
						title="Strikethrough"
						aria-label="Format text with a strikethrough"
					>
						<Strikethrough className="h-2 w-2" />
					</ToggleGroupItem>
				</ToggleGroup>
			)}
			<DropdownMenu>
				<DropdownMenuTrigger asChild>
					<Button variant="outline">
						Open <ChevronDown />
					</Button>
				</DropdownMenuTrigger>
				<DropdownMenuContent>
					<DropdownMenuGroup>
						<DropdownMenuLabel>My Account</DropdownMenuLabel>
						<DropdownMenuItem onSelect={clearSelection}>
							Profile
						</DropdownMenuItem>
						<DropdownMenuItem onSelect={clearSelection}>
							Billing
						</DropdownMenuItem>
						<DropdownMenuItem onSelect={clearSelection}>
							Settings
						</DropdownMenuItem>
					</DropdownMenuGroup>
					<DropdownMenuSeparator />
					<DropdownMenuItem onSelect={clearSelection}>
						GitHub
					</DropdownMenuItem>
					<DropdownMenuItem onSelect={clearSelection}>
						Support
					</DropdownMenuItem>
					<DropdownMenuItem onSelect={clearSelection} disabled>
						API
					</DropdownMenuItem>
				</DropdownMenuContent>
			</DropdownMenu>
		</div>,
		anchorElem,
	);
}

import type { JSX } from 'react';

import { useLexicalComposerContext } from '@lexical/react/LexicalComposerContext';
import Toolbar from '@/components/editor-toolbar';

export default function EditorToolbarPlugin({
	anchorElem = document.body,
}: {
	anchorElem?: HTMLElement;
}): JSX.Element | null {
	const [editor] = useLexicalComposerContext();
	return <Toolbar editor={editor} anchorElem={anchorElem} />;
}

'use client';

import type { JSX } from 'react';

import { DraggableBlockPlugin_EXPERIMENTAL } from '@lexical/react/LexicalDraggableBlockPlugin';
import { useRef, useCallback } from 'react';
import { GripVertical, Plus } from 'lucide-react';

type BlockControlPluginProps = {
	anchorElem?: HTMLElement;
};

export default function EditorBlockControlPlugin({
	anchorElem = document.body,
}: BlockControlPluginProps): JSX.Element {
	const menuRef = useRef<HTMLDivElement>(null);
	const targetLineRef = useRef<HTMLDivElement>(null);
	const draggableBlockElemRef = useRef<HTMLElement | null>(null);

	const handleElementChanged = useCallback((element: HTMLElement | null) => {
		draggableBlockElemRef.current = element;
	}, []);

	const isOnMenu = useCallback((element: HTMLElement): boolean => {
		return !!menuRef.current?.contains(element);
	}, []);

	return (
		<>
			<DraggableBlockPlugin_EXPERIMENTAL
				anchorElem={anchorElem}
				menuRef={menuRef}
				targetLineRef={targetLineRef}
				onElementChanged={handleElementChanged}
				menuComponent={
					<div
						ref={menuRef}
						className="group flex items-center gap-0.5 rounded p-0.5 px-px opacity-0 absolute left-0 top-0 will-change-transform"
					>
						<button
							type="button"
							className="flex items-center justify-center w-6 h-6 p-0 border-none bg-transparent cursor-pointer opacity-30 rounded group-hover:opacity-60 hover:opacity-100! hover:bg-neutral-200"
							aria-label="Add block"
						>
							<Plus size={18} />
						</button>
						<div className="flex items-center justify-center w-6 h-6 opacity-30 cursor-grab active:cursor-grabbing rounded group-hover:opacity-60 hover:opacity-100! hover:bg-neutral-200">
							<GripVertical size={18} />
						</div>
					</div>
				}
				targetLineComponent={
					<div
						ref={targetLineRef}
						className="ib-block-control-target-line pointer-events-none bg-sky-400 h-1 absolute left-0 top-0 opacity-0 will-change-transform"
					/>
				}
				isOnMenu={isOnMenu}
			/>
		</>
	);
}

import {
	$getSelection,
	$isParagraphNode,
	$isRangeSelection,
	$isTextNode,
	getDOMSelection,
	LexicalEditor,
} from 'lexical';
import { useCallback, useEffect, useRef, useState } from 'react';
import { mergeRegister } from '@lexical/utils';
import { getSelectedNode } from '../lib/editor-utils';

export type EditorToolbarState = {
	isVisible: boolean;
	isBold: boolean;
	isItalic: boolean;
	isUnderline: boolean;
	isUppercase: boolean;
	isLowercase: boolean;
	isCapitalize: boolean;
	isStrikethrough: boolean;
	isSubscript: boolean;
	isSuperscript: boolean;
};

const DEFAULT_STATE: EditorToolbarState = {
	isVisible: false,
	isBold: false,
	isItalic: false,
	isUnderline: false,
	isUppercase: false,
	isLowercase: false,
	isCapitalize: false,
	isStrikethrough: false,
	isSubscript: false,
	isSuperscript: false,
};

export function useEditorToolbar(editor: LexicalEditor): EditorToolbarState {
	const [toolbarState, setEditorToolbarState] =
		useState<EditorToolbarState>(DEFAULT_STATE);
	const isMouseDownRef = useRef(false);

	const update = useCallback(
		(forceShow = false) => {
			editor.getEditorState().read(() => {
				if (editor.isComposing()) return;

				// Don't show toolbar while mouse is down (still selecting)
				if (isMouseDownRef.current && !forceShow) {
					return;
				}

				const selection = $getSelection();
				const nativeSelection = getDOMSelection(editor._window);
				const rootElement = editor.getRootElement();

				if (
					nativeSelection !== null &&
					(!$isRangeSelection(selection) ||
						rootElement === null ||
						!rootElement.contains(nativeSelection.anchorNode))
				) {
					setEditorToolbarState(DEFAULT_STATE);
					return;
				}

				if (!$isRangeSelection(selection)) return;

				const node = getSelectedNode(selection);

				const hasText = selection.getTextContent() !== '';
				const isText =
					hasText && ($isTextNode(node) || $isParagraphNode(node));

				const rawTextContent = selection
					.getTextContent()
					.replace(/\n/g, '');
				const isCollapsedWhitespaceSelection =
					!selection.isCollapsed() && rawTextContent === '';

				if (!isText || isCollapsedWhitespaceSelection) {
					setEditorToolbarState(DEFAULT_STATE);
					return;
				}

				setEditorToolbarState({
					isVisible: true,
					isBold: selection.hasFormat('bold'),
					isItalic: selection.hasFormat('italic'),
					isUnderline: selection.hasFormat('underline'),
					isUppercase: selection.hasFormat('uppercase'),
					isLowercase: selection.hasFormat('lowercase'),
					isCapitalize: selection.hasFormat('capitalize'),
					isStrikethrough: selection.hasFormat('strikethrough'),
					isSubscript: selection.hasFormat('subscript'),
					isSuperscript: selection.hasFormat('superscript'),
				});
			});
		},
		[editor],
	);

	useEffect(() => {
		const rootElement = editor.getRootElement();
		if (!rootElement) return;

		const handleMouseDown = () => {
			isMouseDownRef.current = true;
			// Hide toolbar when starting a new selection
			setEditorToolbarState(DEFAULT_STATE);
		};

		const handleMouseUp = () => {
			isMouseDownRef.current = false;
			// Show toolbar after mouse is released
			update(true);
		};

		rootElement.addEventListener('mousedown', handleMouseDown);
		document.addEventListener('mouseup', handleMouseUp);

		return () => {
			rootElement.removeEventListener('mousedown', handleMouseDown);
			document.removeEventListener('mouseup', handleMouseUp);
		};
	}, [editor, update]);

	useEffect(() => {
		document.addEventListener('selectionchange', () => update());
		return () =>
			document.removeEventListener('selectionchange', () => update());
	}, [update]);

	useEffect(() => {
		return mergeRegister(
			editor.registerUpdateListener(() => update()),
			editor.registerRootListener(() => {
				if (editor.getRootElement() === null) {
					setEditorToolbarState(DEFAULT_STATE);
				}
			}),
		);
	}, [editor, update]);

	return toolbarState;
}

'use client';

import { EditorThemeClasses } from 'lexical';
import { useState } from 'react';
import { Editor } from '../lib/Editor';

export interface UseCreateEditorOptions {
	name?: string;
	theme?: EditorThemeClasses;
}

export const useCreateEditor = (options: UseCreateEditorOptions = {}) => {
	const [editor] = useState(() => new Editor(options));

	return editor;
};

import { $isAtNodeEnd } from '@lexical/selection';
import { ElementNode, RangeSelection, TextNode } from 'lexical';

const VERTICAL_GAP = 10;
const HORIZONTAL_OFFSET = 5;

export function setFloatingElemPosition(
	targetRect: DOMRect | null,
	floatingElem: HTMLElement,
	anchorElem: HTMLElement,
	isLink: boolean = false,
	verticalGap: number = VERTICAL_GAP,
	horizontalOffset: number = HORIZONTAL_OFFSET,
): void {
	const scrollerElem = anchorElem.parentElement;

	if (targetRect === null || !scrollerElem) {
		floatingElem.style.opacity = '0';
		floatingElem.style.top = '-10000px';
		floatingElem.style.left = '-10000px';
		return;
	}

	const floatingElemRect = floatingElem.getBoundingClientRect();
	const anchorElementRect = anchorElem.getBoundingClientRect();
	const editorScrollerRect = scrollerElem.getBoundingClientRect();

	let top = targetRect.top - floatingElemRect.height - verticalGap;
	let left = targetRect.left - horizontalOffset;

	const selection = window.getSelection();
	if (selection && selection.rangeCount > 0) {
		const range = selection.getRangeAt(0);
		const textNode = range.startContainer;
		if (textNode.nodeType === Node.ELEMENT_NODE || textNode.parentElement) {
			const textElement =
				textNode.nodeType === Node.ELEMENT_NODE
					? (textNode as Element)
					: (textNode.parentElement as Element);
			const textAlign = window.getComputedStyle(textElement).textAlign;

			if (textAlign === 'right' || textAlign === 'end') {
				left =
					targetRect.right -
					floatingElemRect.width +
					horizontalOffset;
			}
		}
	}

	if (top < editorScrollerRect.top) {
		top +=
			floatingElemRect.height +
			targetRect.height +
			verticalGap * (isLink ? 9 : 2);
	}

	if (left + floatingElemRect.width > editorScrollerRect.right) {
		left =
			editorScrollerRect.right -
			floatingElemRect.width -
			horizontalOffset;
	}

	if (left < editorScrollerRect.left) {
		left = editorScrollerRect.left + horizontalOffset;
	}

	top -= anchorElementRect.top;
	left -= anchorElementRect.left;

	floatingElem.style.opacity = '1';
	floatingElem.style.top = `${top}px`;
	floatingElem.style.left = `${left}px`;
}

export function getSelectedNode(
	selection: RangeSelection,
): TextNode | ElementNode {
	const anchor = selection.anchor;
	const focus = selection.focus;
	const anchorNode = selection.anchor.getNode();
	const focusNode = selection.focus.getNode();
	if (anchorNode === focusNode) {
		return anchorNode;
	}
	const isBackward = selection.isBackward();
	if (isBackward) {
		return $isAtNodeEnd(focus) ? anchorNode : focusNode;
	} else {
		return $isAtNodeEnd(anchor) ? anchorNode : focusNode;
	}
}

export function getDOMRangeRect(
	nativeSelection: Selection,
	rootElement: HTMLElement,
): DOMRect {
	const domRange = nativeSelection.getRangeAt(0);

	let rect;

	if (nativeSelection.anchorNode === rootElement) {
		let inner = rootElement;
		while (inner.firstElementChild != null) {
			inner = inner.firstElementChild as HTMLElement;
		}
		rect = inner.getBoundingClientRect();
	} else {
		rect = domRange.getBoundingClientRect();
	}

	return rect;
}

import {
	AnyLexicalExtension,
	defineExtension,
	EditorThemeClasses,
	Klass,
	LexicalNode,
} from 'lexical';
import { baseNodes } from './base-nodes';
import {
	HorizontalRuleExtension,
	TabIndentationExtension,
} from '@lexical/extension';
import { CheckListExtension, ListExtension } from '@lexical/list';
import { editorTheme } from './default-theme';

export interface EditorOptions {
	name?: string;
	theme?: EditorThemeClasses;
	nodes?: Array<Klass<LexicalNode>>;
	extensions?: AnyLexicalExtension[];
}

export class Editor {
	private _lexicalExtension: AnyLexicalExtension;

	constructor(options: EditorOptions) {
		const {
			name = 'editor',
			nodes = [],
			extensions = [],
			theme = {},
		} = options;
		this._lexicalExtension = defineExtension({
			name,
			nodes: [...baseNodes, ...nodes],
			dependencies: [
				HorizontalRuleExtension,
				ListExtension,
				CheckListExtension,
				TabIndentationExtension,
				...extensions,
			],
			theme: { ...editorTheme, ...theme },
		});
	}

	get lexicalExtension() {
		return this._lexicalExtension;
	}
}

import { HorizontalRuleNode } from '@lexical/extension';
import { ListItemNode, ListNode } from '@lexical/list';
import { HeadingNode, QuoteNode } from '@lexical/rich-text';
import { Klass, LexicalNode } from 'lexical';

export const baseNodes: Array<Klass<LexicalNode>> = [
	HeadingNode,
	ListNode,
	ListItemNode,
	QuoteNode,
	HorizontalRuleNode,
];

.EditorTheme__blockCursor {
    display: block;
    pointer-events: none;
    position: absolute;
}

.EditorTheme__blockCursor:after {
    content: '';
    display: block;
    position: absolute;
    top: -2px;
    width: 20px;
    border-top: 1px solid rgb(55, 53, 47);
    animation: CursorBlink 1.1s steps(2, start) infinite;
}

@keyframes CursorBlink {
    to {
        visibility: hidden;
    }
}

.EditorTheme__code {
    background-color: rgb(247, 246, 243);
    font-family: 'SFMono-Regular', Menlo, Consolas, 'PT Mono', 'Liberation Mono', Courier, monospace;
    display: block;
    padding: 32px 16px 32px 52px;
    line-height: 1.5;
    font-size: 0.875em;
    margin: 4px 0;
    overflow-x: auto;
    position: relative;
    tab-size: 2;
    border-radius: 3px;
    color: rgb(55, 53, 47);
}

.EditorTheme__code:before {
    content: attr(data-gutter);
    position: absolute;
    left: 0;
    top: 0;
    padding: 32px 8px;
    color: rgba(55, 53, 47, 0.4);
    white-space: pre-wrap;
    text-align: right;
    min-width: 32px;
    background-color: transparent;
    border-right: none;
}

.EditorTheme__hr {
    padding: 0;
    border: none;
    margin: 6px 0;
    cursor: pointer;
    height: 1px;
}

.EditorTheme__hr:after {
    content: '';
    display: block;
    height: 1px;
    background-color: rgba(55, 53, 47, 0.16);
}

.EditorTheme__hr.EditorTheme__hrSelected {
    outline: none;
    background-color: rgba(35, 131, 226, 0.2);
}

.EditorTheme__hr.EditorTheme__hrSelected:after {
    background-color: rgb(35, 131, 226);
}

.EditorTheme__listItemChecked,
.EditorTheme__listItemUnchecked {
    position: relative;
    margin-left: 0;
    padding-left: 24px;
    list-style-type: none;
    outline: none;
    display: block;
    min-height: 1.5em;
    line-height: 1.5;
}

.EditorTheme__listItemChecked>*,
.EditorTheme__listItemUnchecked>* {
    margin-left: 0;
}

.EditorTheme__listItemUnchecked:before,
.EditorTheme__listItemChecked:before {
    content: '';
    width: 16px;
    height: 16px;
    top: 50%;
    left: 0;
    cursor: pointer;
    display: block;
    position: absolute;
    transform: translateY(-50%);
    border-radius: 3px;
}

.EditorTheme__listItemChecked {
    color: rgba(55, 53, 47, 0.65);
    text-decoration: line-through;
}

.EditorTheme__listItemUnchecked:focus:before,
.EditorTheme__listItemChecked:focus:before {
    box-shadow: 0 0 0 2px rgb(35, 131, 226);
}

.EditorTheme__listItemUnchecked:before {
    border: 1.5px solid rgba(55, 53, 47, 0.4);
    background: transparent;
}

.EditorTheme__listItemUnchecked:hover:before {
    background: rgba(55, 53, 47, 0.05);
}

.EditorTheme__listItemChecked:before {
    border: none;
    background-color: rgb(35, 131, 226);
}

.EditorTheme__listItemChecked:after {
    content: '';
    cursor: pointer;
    border-color: #fff;
    border-style: solid;
    position: absolute;
    display: block;
    top: 50%;
    width: 3.5px;
    left: 6px;
    height: 7px;
    transform: translateY(-55%) rotate(45deg);
    border-width: 0 2px 2px 0;
}

.EditorTheme__tabNode {
    position: relative;
    text-decoration: none;
}

.EditorTheme__tabNode.EditorTheme__textUnderline::after {
    content: '';
    position: absolute;
    left: 0;
    right: 0;
    bottom: 0.15em;
    border-bottom: 0.1em solid currentColor;
}

.EditorTheme__tabNode.EditorTheme__textStrikethrough::before {
    content: '';
    position: absolute;
    left: 0;
    right: 0;
    top: 0.69em;
    border-top: 0.1em solid currentColor;
}

.EditorTheme__tabNode.EditorTheme__textUnderlineStrikethrough::before,
.EditorTheme__tabNode.EditorTheme__textUnderlineStrikethrough::after {
    content: '';
    position: absolute;
    left: 0;
    right: 0;
}

.EditorTheme__tabNode.EditorTheme__textUnderlineStrikethrough::before {
    top: 0.69em;
    border-top: 0.1em solid currentColor;
}

.EditorTheme__tabNode.EditorTheme__textUnderlineStrikethrough::after {
    bottom: 0.05em;
    border-bottom: 0.1em solid currentColor;
}
